mapper.update:
update t_resident
set is_delete = 1
where id = #{id,jdbcType=BIGINT}

mapper.select:
select id, name, sex, education, politics, IFNULL(xiang_id,"")
xiangId,
IFNULL((select village_name from t_village where id = xiang_id),"") as xiangName,
IFNULL(cun_id,"")
cunId, IFNULL((select village_name from t_village
where id = cun_id),"") as
cunName from t_resident where is_delete=0

	and sex = #{sex,jdbcType=INTEGER}


	and education = #{education,jdbcType=INTEGER}


	and politics = #{politics,jdbcType=INTEGER}


	and xiang_id = #{xiangId,jdbcType=INTEGER}


	and cun_id = #{cunId,jdbcType=INTEGER}


	and name like CONCAT('%', #{name,jdbcType=VARCHAR},'%')

order by create_time desc limit #{offset}, #{show}

select count(1) from t_resident where is_delete=0

	and sex = #{sex,jdbcType=INTEGER}


	and education = #{education,jdbcType=INTEGER}


	and politics = #{politics,jdbcType=INTEGER}


	and xiang_id = #{xiangId,jdbcType=INTEGER}


	and cun_id = #{cunId,jdbcType=INTEGER}


	and name like CONCAT('%', #{name,jdbcType=VARCHAR},'%')

SELECT count(1) value, CASE education WHEN 1 THEN '无文凭' WHEN 2 THEN '小学' WHEN 3 THEN '初中' WHEN 4 THEN '高中' WHEN 5 THEN '大专/高职' WHEN 6 THEN '本科' ELSE '研究生及以上' END AS name FROM t_resident WHERE is_delete = 0
and politics = #{politics,jdbcType=INTEGER}
and xiang_id in
#{item}
and cun_id in
#{item}
GROUP BY education

SELECT count(1) value, CASE politics WHEN 1 THEN '群众' WHEN 2 THEN '共青团员' WHEN 3 THEN '预备党员' ELSE '党员' END AS name FROM t_resident WHERE is_delete = 0
and education = #{education,jdbcType=INTEGER}
and xiang_id in
#{item}
and cun_id in
#{item}
GROUP BY politics

SELECT count(1) AS value, ( SELECT village_name FROM t_village WHERE id = a.xiang_id )AS name FROM ( SELECT * FROM t_resident WHERE is_delete = 0
and xiang_id in
#{item}
and education = #{education,jdbcType=INTEGER}
and politics = #{politics,jdbcType=INTEGER}
)a GROUP BY xiang_id

SELECT count(1)AS value, ( SELECT village_name FROM t_village WHERE id = a.cun_id )AS name FROM ( SELECT * FROM t_resident WHERE is_delete = 0
and cun_id in
#{item,jdbcType=INTEGER}
and education = #{education,jdbcType=INTEGER}
and politics = #{politics,jdbcType=INTEGER}
)a GROUP BY cun_id
